import React, { Component } from "react";
import CollectionsOverview from "../../components/CollectionsOverview";
import { Route } from "react-router-dom";
import CollectionPage from "../CollectionPage";
import { connect } from "react-redux";
import { updateCollections, fetchCollectionsStartAsync } from "../../redux/shop/shopActions";
import WithSpinner from "../../components/HOC/withSpinner";
import { createStructuredSelector } from "reselect";
import { selectCollectionFetching } from "../../redux/shop/shopSelector";

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends Component {

    componentDidMount() {
        this.props.fetchCollectionsStartAsync()
    }
 
  render() {
    const { match } = this.props;
    const { isCollectionFetching } = this.state;
    return (
      <div className="shop-page">
        <Route
          exact
          path={`${match.path}`}
          render={props => (
            <CollectionsOverviewWithSpinner isLoading={isCollectionFetching} {...props} />
          )}
        />
        <Route
          path={`${match.path}/:collectionId`}
          render={props => (
            <CollectionPageWithSpinner isLoading={isCollectionFetching} {...props} />
          )}
        />
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
    isCollectionFetching: selectCollectionFetching
})

function mapDispatchToProps(dispatch) {
  return {
    fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ShopPage);
