import React, { Component } from "react";
import CollectionsOverview from "../../components/CollectionsOverview";
import { Route } from "react-router-dom";
import CollectionPage from "../CollectionPage";
import { connect } from "react-redux";
import {
  fetchCollectionsStartAsync
} from "../../redux/shop/shopActions";
import WithSpinner from "../../components/HOC/withSpinner";
import { createStructuredSelector } from "reselect";
import { selectCollectionFetching, selectIsCollectionLoaded } from "../../redux/shop/shopSelector";
import { CollectionsOverviewContainer } from "../../components/CollectionsOverview/index.container";

const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends Component {
  componentDidMount() {
    this.props.fetchCollectionsStartAsync();
  }

  render() {
    const { match ,isCollectionLoaded} = this.props;
    return (
      <div className="shop-page">
        <Route
          exact
          path={`${match.path}`}
        //   render={props => (
        //     <CollectionsOverviewWithSpinner
        //       isLoading={isCollectionFetching}
        //       {...props}
        //     />
        //   )}
        component = {CollectionsOverviewContainer}
        />
        <Route
          path={`${match.path}/:collectionId`}
          render={props => (
            <CollectionPageWithSpinner
              isLoading={!isCollectionLoaded}
              {...props}
            />
          )}
        />
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  isCollectionLoaded: selectIsCollectionLoaded
});

function mapDispatchToProps(dispatch) {
  return {
    fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ShopPage);
