import React from 'react'
import './index.scss';

export default function MenuItem({title, imageUrl, size}) {
    return (
        <div 
            style={{backgroundImage:`url(${imageUrl})`}} 
            className={`menu-item ${size}`}>
            <div className="content">
                <h1 className="title">
                    {title}
                </h1>
                <span className='subtitle'>SHOP NOW</span>
            </div>
        </div>
    )
}
