import {takeLatest, put, all, call} from 'redux-saga/effects';
import * as actionType from '../constants/index';
import { googleProvider, auth, createUserProfileDoc } from '../../firebase/firebase.utils';
import { googleSignInSuccess, googleSignInFailure } from './userActions';

export function * signInWithGoogle() {
    
    try {
        const {user} = yield auth.signInWithPopup(googleProvider);
        const userRef = yield call(createUserProfileDoc,user);
        const snapshot = yield userRef.get();
        yield put(googleSignInSuccess({id:snapshot.id, ...snapshot.data()}))
    } catch (error) {
        
    }
}

export function* onGoogleSignInStart() {
    yield takeLatest(actionType.GOOGLE_SIGN_IN_START,signInWithGoogle)
}

export function* userSagas() {
    yield all([
        call(onGoogleSignInStart)
    ])
}
